import { createOvermind } from 'overmind';
import { Provider } from 'overmind-react';

import Link from 'next/link';
import { useRouter } from 'next/router';
import React from 'react';
import useSWR from 'swr';

//Overmind state management

import {config, useOvermind } from '../overmind';

//Bootstrap css
import 'bootstrap/dist/css/bootstrap.min.css';
//Index.scss file should be above layout layout and components after Bootstrap css
import styles from './index.scss';

//Custom Layout Components
import Layout from '../components/Layout';
import RSVPForm from '../components/rsvp-form'; 

const overmind = createOvermind(config, {
    devtools: 'localhost:3000'
});

function fetcher(url) {
    return fetch(url).then(r => r.json())
}

export default function Index() {
    const { state, actions } = useOvermind();
    const { query } = useRouter();

    const { data, error } = useSWR(`/api/randomQuote${query.author ? '?author=' + query.author : ''}`, fetcher);

    const author = data?.author;
    let quote = data?.quote;

    if (!data) quote = "Loading...";
    if (error) quote = "Failed to fetch quotes";

    return (
        <Layout className="">

            <div className="mb-5">
                <div className={styles.quote}>{quote}</div>
                {author && <span className={styles.author}>- {author}</span>}

                <Link href="/rsvp-scanner">
                    <a>Goto RSVP Scanner</a>
                </Link>
            </div>

            <RSVPForm></RSVPForm>

            <style jsx>{`

            `}</style>
        </Layout>
    )
}
