import * as React from 'react';
import RSVPPersonal from './personal';
import RSVPEntity from './entity';
import RSVProxyHolder from './proxy-holder';
import { useOvermind } from '../../overmind';


function RSVPForm(props) {
    const { state } = useOvermind();

    const handleRSVPTypeChange = function (e) {
        e.preventDefault();
        // this.setState({ rsvpType: e.target.value });
        state.rsvp.type = e.target.value;

        console.log('changed triggered!', e.target.value);
    }

    return (
        <div className="rsvp-form-wrapper">

            <h1>{state.rsvp.title}</h1>
            <div className="form-group">
                <label htmlFor="">Select RSVP Type: </label>
                <select className="form-control" value={state.rsvp.type} onChange={this.handleRSVPTypeChange}>
                    {rsvpTypes.map((type, i) => (<option key={i} value={type.value}>{type.text}</option>))}
                </select>
            </div>
            {state.rsvp.type === 'personal' && <RSVPPersonal />}
            {state.rsvp.type === 'entity' && <RSVPEntity />}
            {state.rsvp.type === 'proxyholder' && <RSVProxyHolder />}

        </div>
    )
}

export default RSVPForm;
