import * as React from 'react';
import RSVPPersonal from './personal';
import RSVPEntity from './entity';
import RSVProxyHolder from './proxy-holder';

const rsvpTypes = [
    {
        text: "Personal",
        value: "personal",
    },
    {
        text: "Entity",
        value: "entity",
    },
    {
        text: "Proxy Holder",
        value: "proxyholder",
    },
];

class RSVPForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            rsvpType: 'personal'
        }

        this.handleRSVPTypeChange = this.handleRSVPTypeChange.bind(this);
    }

    handleRSVPTypeChange(e) {
        e.preventDefault();
        this.setState({ rsvpType: e.target.value });
        // console.log('changed triggered!', this.state.rsvpType);
    }

    render() {
        return (
            <div className="rsvp-form-wrapper">
                {state.rsvp.title}
                <h1>Online Registration / RSVP Online</h1>
                <div className="form-group">
                    <label htmlFor="">Select RSVP Type: </label>
                    <select className="form-control" value={this.state.rsvpType} onChange={this.handleRSVPTypeChange}>
                        {rsvpTypes.map((type, i) => (<option key={i} value={type.value}>{type.text}</option>))}
                    </select>
                </div>
                {this.state.rsvpType === 'personal' && <RSVPPersonal />}
                {this.state.rsvpType === 'entity' && <RSVPEntity />}
                {this.state.rsvpType === 'proxyholder' && <RSVProxyHolder />}

            </div>
        )
    }
}

export default RSVPForm;
