import React, { useEffect, useRef } from 'react';
import { useForm, ErrorMessage } from 'react-hook-form';
import { useOvermind } from '../../../overmind';

import { phonePattern1, emailPattern } from '../../../util/constants';
import { reCaptchaInitialize, verifyCaptcha } from '../../../util/index';

export default function RSVPPersonal(props) {

    const { state, actions } = useOvermind();
    const { register, errors, handleSubmit, watch, formState } = useForm({
        mode: 'onChange',
        validateCriteriaMode: "all"
    });

    const { 
        numberKTUR, numberSID, name, 
        numberKTPOrPassport, address, numberOfShares, phoneNumber,
        email,
        uploadID,
        captcha,
        submitButton,
        successMsgTitle,
        successMsg,
        failedMsgTitle,
        failedMsg,
        redirectURL
    } = state.rsvp.personal && state.rsvp.personal.data || {};


    const watchNoKtur = watch("numberKTUR");
    const watchNoSID = watch("numberSID");
    const watchHidCaptcha = watch('hidGRecaptchaElement');

    const onSubmit = (data, e) => {
        console.log("Form submitted ", data, e)
    };

    // Similar to componentDidMount and componentDidUpdate:
    useEffect(() => {
        // if(!window.grecaptcha) reCaptchaInitialize('v2');

        // console.log('watchHidCaptcha', watchHidCaptcha);

    }, [watchHidCaptcha]);

    useEffect(()=> {
        if(state.rsvp.isSIDKTPVerified){
            // verifyCaptcha
            verifyCaptcha('hidGRecaptchaElement');
        }
        console.log('watchNoKtur', watchNoKtur);
        console.log('watchNoSID', watchNoSID);

        if((watchNoKtur && watchNoKtur.length > 6) && (watchNoSID  && watchNoSID.length > 6)) {
            console.log('trigger api call to verify use is having valid sid and ktp ');

            // actions.rsvp.verifyValidUser();

            actions.rsvp.updateSIDKTPVerifedStatus();

            // if(window.grecaptcha) grecaptcha.reset('gRecaptchaElement');
            if(!window.grecaptcha) reCaptchaInitialize('v2');
        }
    });

    return (
        <form className="form" onSubmit={handleSubmit(onSubmit)}>
            <h2>Personal</h2>
           <pre> {state.rsvp.personal.data && JSON.stringify(state.rsvp.personal.data, null, 2)}</pre>
            <div className="form-group">
                <label htmlFor={numberKTUR.name} className="col-form-label">No KTUR <span className="required">*</span></label>
                <div className="">
                    <input id={numberKTUR.name} name={numberKTUR.name} 
                        placeholder={numberKTUR.placeholder}
                        ref={register({ 
                            required: numberKTUR.validation.required_msg,
                        minLength: {
                            value: 6,
                            message: numberKTUR.validation.minlength_msg
                          }
                     })} 
                        type="text" className="form-control" />
                    <ErrorMessage as="p" errors={errors} name={numberKTUR.name} />
                </div>
            </div>

            <div className="form-group">
                <label htmlFor={numberSID.name} className="col-form-label">No SID/AKSes.KSEI <span className="required">*</span></label>
                <div className="">
                    <input id={numberSID.name} name={numberSID.name} 
                        placeholder={numberSID.placeholder}
                        ref={register({ 
                            required: numberSID.validation.required_msg,
                            minLength: {
                                value: 6,
                                message: numberSID.validation.minlength_msg
                            }
                        })} 
                        type="text" 
                        className="form-control" />
                    <ErrorMessage as="p" errors={errors} name={numberSID.name} />
                </div>
            </div>
            {/* {'state.rsvp.isSIDKTPVerified : ' + state.rsvp.isSIDKTPVerified} */}
            {
                state.rsvp.isSIDKTPVerified === true ? (
                <React.Fragment> 
                    <div className="form-group">
                        <label htmlFor={name.name} className="col-form-label">Name <span className="required">*</span></label>
                        <div className="">
                            <input id={name.name} 
                                name={name.name} 
                                ref={register({ 
                                    required: name.validation.required_msg,
                                 })} 
                                 type="text" className="form-control"
                            />
                            <ErrorMessage as="p" errors={errors} name={name.name} />
                        </div>
                    </div>

                    <div className="form-group">
                        <label htmlFor={passport.name} className="col-form-label">NIK (KTP)/Passport <span className="required">*</span></label>
                        <div className="">
                            <input id={passport.name} name={passport.name} 
                                placeholder={passport.placeholder}
                                ref={register({ 
                                    required: 'NIK (KTP)/Passport is required'
                                 })} 
                                 type="text" 
                                 className="form-control" />
                            <ErrorMessage as="p" errors={errors} name={passport.name} />
                        </div>
                    </div>

                    <div className="form-group">
                        <label htmlFor="address" className="col-form-label">Address <span className="required">*</span></label>
                        <div className="">
                            <input name="address" ref={register({ required: 'Address is required' })} type="text" className="form-control" id="address" />
                            <ErrorMessage as="p" errors={errors} name="address" />
                        </div>
                    </div>

                    <div className="form-group">
                        <label htmlFor="numberOfShares" className="col-form-label">Jumlah Saham (sesuai KTUR) <span className="required">*</span></label>
                        <div className="">
                            <input name="numberOfShares" ref={register({ required: 'Jumlah Saham (sesuai KTUR) is required' })} type="text" className="form-control" id="numberOfShares" />
                            <ErrorMessage as="p" errors={errors} name="numberOfShares" />
                        </div>
                    </div>

                    <div className="form-group">
                        <label htmlFor="phoneNumber" className="col-form-label">Phone/Mobile <span className="required">*</span></label>
                        <div className="">
                            <input name="phoneNumber" ref={register({
                                required: 'Phone number is required',
                                pattern: {
                                    value: phonePattern1,
                                    message: "Invalid phone number"
                                }
                            }
                            )} type="text" className="form-control" id="phoneNumber" />
                            <ErrorMessage as="p" errors={errors} name="phoneNumber" />
                        </div>
                    </div>

                    <div className="form-group">
                        <label htmlFor="email" className="col-form-label">Email <span className="required">*</span></label>
                        <div className="">
                            <input name="email" 
                            ref={register({ required: 'Email is required',
                            pattern: {
                                value: emailPattern,
                                message: "Invalid email"
                            } })} 
                            type="email" className="form-control" id="email" />
                            <ErrorMessage as="p" errors={errors} name="email" />
                        </div>
                    </div>

                    <div className="form-group">
                        {/* <label htmlFor="uploadID" className="col-form-label">Upload ID <span className="required">*</span></label> */}
                        {/* <div className="">
                            <input name="uploadID" ref={register({ required: 'This is required' })} type="file" className="form-control" id="uploadID" />
                        </div> */}
                        <div className="file-box">
                            <label className="input-file-label"><a className="button button--2 button--axiata">Upload ID</a>
                                No file chosen
                                <input name="uploadID" ref={register({ required: 'Upload ID is required' })} type="file" className="input-file" id="uploadID" />
                            </label>
                        </div>
                        <div className="file-info">
                            <p>Ukuran berkas harus kurang dari 10 MB.s</p>
                            <p>Jenis file yang diperbolehkan: .jpg , .jpeg, .png, .pdf</p>
                        </div>
                        <ErrorMessage as="p" errors={errors} name="uploadID" />
                    </div>

                    <div className="form-group">
                        <div className="g-recaptcha" id="gRecaptchaElement"></div>
                        <input type="hidden" name="hidGRecaptchaElement" defaultValue={state.rsvp.captchaValue} 
                            ref={register({required: 'Please validate captcha'})}/>
                        <ErrorMessage as="p" errors={errors} name="hidGRecaptchaElement" />
                    </div>

                    <div className="clearfix">
                        <div className="form-check form-check-inline">
                            <input name="userAcceptance" id="userAcceptance" ref={register({ required: 'Please accept the terms and condition' })} type="checkbox" className="form-check-label" />
                            <label htmlFor="userAcceptance" className="col-form-label pl-1"> Check to agree xl axiata terms and conditions.</label>
                        </div>
                        <ErrorMessage as="p" errors={errors} name="userAcceptance" />
                    </div>

                    <div className="form-group text-center">
                        <div className="">
                        {/* <pre>{JSON.stringify(formState, null, 2)}</pre> */}

                            <button type="submit" className="button">Submit</button>
                        </div>
                    </div>
                </React.Fragment>) : (<div className="text-center text-info">Enter No KTUR &amp; No SID/AKSes.KSEI to verify your status</div>) }

        </form>
    );
}
